import React from "react";

class Instruction extends React.Component {
    render() {
        return (
            <div className={"control-menu__import"}>
                <p className={"control-menu__link"} id={"import"}>
                    {this.props.text}
                </p>
                <button onClick={() => this.copyToClipboard("import")} className={"control-menu__button"}>
                    Copy
                </button>
            </div>
        )
    }
}

export default Instruction;
