import React, { Component } from 'react';
import '../../assets/css/ControlCenter/Nav.scss';

class NavBtn extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isHovered: false,
            isActive: false,
        }
    }

    render() {
        const {btnName} = this.props;
        const underline = this.isHovered() && <div className="nav__underline"/>

        return(
            <div>
                <button className="nav__btn nav__btn_highlight"
                        onMouseEnter={this.toUnderline}>
                    {btnName}
                </button>
                {underline}
            </div>
        )
    }

    toUnderline = () => {
        this.setState({
            isHovered: !this.state.isHovered,
        })
    }

    isHovered = () => {
        return this.state.isHovered;

    }
}


export default NavBtn;
