import React from 'react'
import { ThemeContextConsumer } from './ThemeContext'

function ThemeSwitcher(props) {
    return (
        <ThemeContextConsumer>
            {context => (
                <button className="header__btn"
                        onClick={context.toggleTheme}>
                    <span>to light</span>
                </button>
            )}
        </ThemeContextConsumer>
    )
}


export default ThemeSwitcher
