import React, { Component } from 'react';
import '../../assets/css/ControlCenter/ControlMenu.scss';
import {text} from "@fortawesome/fontawesome-svg-core";

class StyleMenu extends Component {
    state = {
        text: "Copy",
        isDisabled: false
    }
    render () {
        let btn;
        if (!this.state.isDisabled) {
            btn = <button onClick={this.copyToClipboard} className={"control-menu__button"}>
                            Copy
                        </button>
        } else {
            btn = <button disabled={true} onClick={this.copyToClipboard} className={"control-menu__button control-menu__button_disabled"}>
                            Copied
                        </button>
        }
        return (
            <div className="control-menu control-menu_row">
                <p>To import a component:</p>
                <div className={"control-menu__import"}>
                    <p className={"control-menu__link"} id={"item-to-copy"}>
                        import "./component/sdkdk/fk"
                    </p>
                    {btn}
                    {/*<button disabled={this.state.isDisabled} onClick={this.copyToClipboard} className={"control-menu__button"}>*/}
                    {/*    {this.state.text}*/}
                    {/*</button>*/}
                </div>
                <p>Then you can use it:</p>
                <div className={"control-menu__insert"}>
                    <p className={"control-menu__link"} id={"item-to-copy"}>
                        Component className={"ng-fl dm-dk gm-rr"}
                    </p>
                    {btn}
                    {/*<button disabled={this.state.isDisabled} onClick={this.copyToClipboard} className={"control-menu__button"}>*/}
                    {/*    {this.state.text}*/}
                    {/*</button>*/}
                </div>
            </div>
        )
    }
     copyToClipboard = () => {
        let str = document.getElementById('item-to-copy').innerText;
        let el = document.createElement('textarea');
        el.value = str;
        document.body.appendChild(el);
        el.select();
        document.execCommand('copy');
        document.body.removeChild(el);
        this.setState({
            text: "Copied",
            isDisabled: true
        })
         setTimeout(this.changeText, 1500)
    }
    changeText = () => {
        this.setState({
            text: "Copy",
            isDisabled: false
        })
    }
}

export default StyleMenu;
