import React, { Component } from 'react';
import {setComponentState} from "../../../../../store/libraryState/actions";
import {connect} from "react-redux";

class TextForm extends Component {
    state = {
        value: '',
    }

    // метод жизненного цикла, позволяющий синхронизировать состояние формы со стилем компонента
    static getDerivedStateFromProps(props, state) {
        const {styleType, componentStyles} = props;

        // синхронизация значения формы и стиля компонента
        if (componentStyles[styleType] !== state.value) {
            return { value: componentStyles[styleType] }
        }

        // в ином случае оставить без изменений
        return null;
    }

    render () {
        const {label} = this.props;

        return (
            <form className="form">
                <label className="form__label">{label}</label>
                <div className="form__wrapper form__wrapper_long">
                    <textarea className="form__textarea"
                              value={this.state.value}
                              onChange={this.handleChange}>
                    </textarea>
                </div>
            </form>
        )
    }

    // метод передает изменения с помощью функции-колбэка
    handleChange = (event) => {
        const {setComponentState, styleType, componentName, isUserComponent} = this.props;
        const value = event.target.value;

        if (isUserComponent) {
            console.log("user component");
        } else {
            setComponentState(componentName, styleType, value);
        }

        this.setState({
            value: value,
        })
    }
}

const mapStateToProps = (state) => {
    const isUserComponent = state.currentComponent.isUserComponent;
    const componentName = state.currentComponent.componentName;
    const userComponentName = state.currentComponent.userComponentName;
    const componentStyles = isUserComponent ? state.userLibrary[userComponentName] : state.libraryState[componentName];


    return {
        componentStyles,
        componentName,
    }
}

const mapDispatchToProps = {
    setComponentState
}


export default connect(mapStateToProps, mapDispatchToProps)(TextForm);
