import React, { useState, useEffect } from "react";
import '../assets/css/App.scss';
import Sidebar from "./AppMenu/Sidebar";
import Header from "./AppMenu/Header";
import ThemeContext from "./ThemeControl/ThemeContext";
import Workspace from "./Workspace";
import {connect, Provider} from "react-redux";
import {createStore} from "redux";
import rootReducer from "../store/reducers";
import axios from "axios";


const store = createStore(rootReducer);

// Компонент App несет в себе функцию отображения всего приложения в целом
function App(props) {
    const { appTheme } = props
    console.log(appTheme)
    const [userComponents, setUserComponents] = useState([])

    useEffect(() => {
        axios({
            method: "GET",
            url: "http://127.0.0.1:8000/api/"
        }).then(response => {
            const userComponents = response.data
            setUserComponents(userComponents)
        })
    }, [setUserComponents])

    if (!userComponents || userComponents.length === 0) {
        return <p>данных нет</p>
    }

    return (
        <Provider store={store}>
            <div>

            </div>
            {/*<ThemeContext.Provider value={ { theme: "light", toggleTheme: this.toggleTheme } }>*/}
            {/*    <main className={`app app_${"light"}`}>*/}
            {/*        <section className="app__menu">*/}
            {/*            <Header />*/}
            {/*            <Sidebar />*/}
            {/*        </section>*/}
            {/*        <Workspace />*/}
            {/*    </main>*/}
            {/*</ThemeContext.Provider>*/}
        </Provider>
    );
}


