import React, { Component } from 'react';
import '../../assets/css/ControlCenter/ControlMenu.scss';

class StyleMenu extends Component {
    render () {
        return (
            <div className="control-menu control-menu_row">
                <p className={"control-menu__link"}>
                    Lorem ipsum dolor sit amet,
                    consectetur adipisicing elit.
                </p>
                <button onClick={this.copyLink} id={"myInput"} className={"control-menu__button"}>Copy</button>
            </div>
        )
    }
    copyLink = () => {
        let copyText = document.getElementById("myInput");
        copyText.select();
        document.execCommand("copy");
        alert("Copied the text: " + copyText.value);
    }
}

export default StyleMenu;
