import React, {Component} from 'react'
import list from '../list.json'
import './assets/css/Inside.scss'

class Inside extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: this.props.id + 1,
            isActive: false
        }
    }

    render () {
        const inside = this.state.isActive && <Inside id={this.state.id} />

        return (
            <div className="tree-element">

                    {list[this.props.id].dirs.map((dir, index) => (
                        <button className={"tree-element__dir"} key={index} onClick={this.deep}>{dir}</button>
                    ))}

                {inside}


                    {list[this.props.id].files.map((file, index) => (
                        <button className={"tree-element__file"} key={index}>{file}</button>
                    ))}

            </div>
        )
    }

    deep = () => {
        this.setState((state) => ({ isActive: !state.isActive }))
    }
}

export default Inside;
