import React, { Component } from 'react';

class DropdownMenu extends Component {
    state = {
        value: '',
    }

    render () {
        const {label, elements} = this.props;

        const optionList = elements.map((element, index) => {
           return <option value={element} key={index}>{element}</option>
        });

        return (
            <form className="style-form">
                <label className="style-form__label">{label}</label>
                <select className="style-form__dropdown"
                        value={this.state.value}
                        onChange={this.handleChange}>
                    {optionList}
                </select>
            </form>
        )
    }

    handleChange = (event) => {
        const {returnValue} = this.props;

        this.setState({
            value: event.target.value
        });

        returnValue(this.state.value);
    }
}

export default DropdownMenu;
