import React, { Component } from 'react';
import '../../assets/css/ControlCenter/ControlMenu.scss';
import {text} from "@fortawesome/fontawesome-svg-core";
import Instruction from "./Instruction";

class StyleMenu extends Component {
    render () {
        return (
            <div className="control-menu control-menu_export">
                <p>To import a component:</p>
                <Instruction text={`import Component from "a/b/c/hj"`} id={"import"} />
                <p>Then you can use it:</p>
                <Instruction text={`Component className="bg-red sz-small cl-blue"`} id={"code"} />
                <pre>
                    <code>{`const fs = require("fs");
                        const pattern = /(\.\/|\/\w)[\w\/]+/g;
                        let pathComponents;
                        try {
                        const data = fs.readFileSync("utils/Hub.js", 'utf-8')
                        pathComponents = data.match(pattern).map((item) => {
                        if (item.slice(-3, 0) !== ".js") {
                        return item += ".js"
                            }
                            })
                            } catch (err) {
                                console.error(err)
                        `}
                    </code>
                </pre>
            </div>
        )
    }
}

export default StyleMenu;
