import React, {Component} from 'react'
import list from '../list.json'
import './assets/css/InnerTree.scss'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faFolder } from "@fortawesome/free-regular-svg-icons"
import { faCaretRight } from "@fortawesome/free-solid-svg-icons"
import { faCaretDown } from "@fortawesome/free-solid-svg-icons"

class InnerTree extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: this.props.id + 1,
            isActive: false
        }
    }

    isActive = () => {
        return this.state.isActive;
    }

    render () {
        const folderIcon = <FontAwesomeIcon className="sidebar__icon sidebar__icon_folder" icon={faFolder}/>
        const arrowDown = <FontAwesomeIcon className="sidebar__icon sidebar__icon_arrow" icon={faCaretDown}/>
        const arrowRight = <FontAwesomeIcon className="sidebar__icon sidebar__icon_arrow" icon={faCaretRight}/>

        const arrowIcon = this.isActive() ? arrowDown : arrowRight;

        const listPosition = list[this.props.id];
        const innerTree = this.isActive() && <InnerTree id={this.state.id}/>

        const dirElement = listPosition.dirs.map((dir, index) => (
            <button className={"tree-element__dir"}
                    key={index}
                    onClick={this.goDown}>
                {arrowIcon}
                {folderIcon}
                {dir}
            </button>
        ));

        const fileElement = listPosition.files.map((file, index) => (
            <button className={"tree-element__file"}
                    key={index}>
                {arrowIcon}
                {file}
            </button>
        ));

        return (
            <div className="tree-element">
                {dirElement}
                {innerTree}
                {fileElement}
            </div>
        )
    }

     goDown = () => {
        this.setState((state) => ({ isActive: !state.isActive }))
    }
}

export default InnerTree;
