        this.state = {
            isHovered: false,
            isActive: false,
        }
    }

    render() {
        const {btnName} = this.props;
        // const underline = this.isHovered() && <div className="nav__underline"/>

        return(
            <div>
                <button className="nav__btn nav__btn_highlight">
                    {btnName}
