import React, {Component} from 'react';
import "./Waves.css"

class Waves extends Component {
    render() {
        const colors = {background: this.props.background,
                        color: this.props.color}
        const text = this.props.text ? this.props.text : "Scooby Doo";
        const size = this.props.size;
        if (size === "small") {
            return (<button className={`${this.props.classname} waves wavesSmall`} style={colors}>
                <p>{text}</p>
                <span className={"blob-btn__inner"}>
                    <span className={"blob-btn__blobs"}>
                        <span className={"blob-btn__blob"}></span>
                        <span className={"blob-btn__blob"}></span>
                        <span className={"blob-btn__blob"}></span>
                        <span className={"blob-btn__blob"}></span>
                    </span>
                </span>
            </button>);
        } else if (size === "medium") {
            return (<button className={`${this.props.classname} waves`} style={colors}>{text}</button>);
        } else {
            return (<button className={`${this.props.classname} waves wavesLarge`} style={colors}>{text}</button>);
        }
    }
}

export default Waves;
