import React, {Component} from 'react';
import "./Classic.css"
import StyleReader from "../../../../utils/StyleReader";
import {connect} from "react-redux";

class Classic extends Component {
    render() {
        const {isUserComponent, userComponentName} = this.props;
        let componentStyle;

        if (!isUserComponent) {
            componentStyle = componentsState && this.props.componentStyle;
        } else {
            componentStyle = componentsState && this.props.userComponentStyle;
        }

        const text = componentStyle.text || "Scooby Doo";
        const styleReader = new StyleReader(componentStyle);

        return (
            <button className={this.props.className + " classic-btn"}
                    style={styleReader.style}
                    onClick={ e => this.handleClick(e) }>
                {text}
            </button>
        )
    }

    handleClick(e) {
        e.preventDefault();
    }
}

const mapStateToProps = (state) => {
    const userComponentName = state.currentComponent.userComponentName;

    return {
        componentStyle: state.libraryState.Classic,
        isUserComponent: state.currentComponent.isUserComponent,
        userComponentName: userComponentName,
        userComponentStyle: state.userLibrary[userComponentName],
    }
}

export default connect(mapStateToProps)(Classic);
