import React, {Component} from 'react';
import "./Waves.css"

class Waves extends Component {
    render() {
        const properties = this.props.className.split(" ");
        const colors = {background: '',
                        color: ''}
        let size;
        let allUserClass = [];
        for (let property of properties) {
            let propertyName = property.split("-")[0];
            let value = property.split("-")[1];
            if (propertyName === "cl") {
                colors["color"] = value;
            } else if (propertyName === "bg") {
                colors["background"] = value;
            } else if (propertyName === "sz") {
                size = value;
            } else {
                allUserClass.push(value);
            }
        }
        const text = this.props.text ? this.props.text : "Scooby Doo";
        let fullClass;
        if (size === "small") {
            fullClass = `${allUserClass} buttonWaves Small`
        } else if (size === "medium") {
            fullClass = `${allUserClass} buttonWaves Medium`
        } else {
            fullClass = `${allUserClass} buttonWaves`
        }
        return (<button className={`${fullClass}`} style={colors}>
            <p>{fullClass.join()}</p>
            <span className={"buttonWaves__inner"}>
                    <span className={"buttonWaves__wave"}></span>
                    <span className={"buttonWaves__wave"}></span>
                    <span className={"buttonWaves__wave"}></span>
                    <span className={"buttonWaves__wave"}></span>
                </span>
        </button>);
    }
}

export default Waves;
