import React from 'react';
import '../assets/css/Workspace.scss'
import * as components from "../utils/Hub"
import ControlCenter from "./ControlCenter/ControlCenter";

class Workspace extends React.Component {
    state = {
        //do not touch
        isUpdate: false
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps.userComponentName !== this.props.userComponentName) {
            this.setState({isUpdate: true})
        }
    }

    render() {
        const {themeContext} = this.props;
        const {userComponentName} = this.props;
        const Component = components[userComponentName];
        const currentComponent = userComponentName && <Component className={`${this.buildClassName()}`}>
                                                            {this.state.text}
                                                      </Component>;
        let currentMenu;
        if (this.state.isUpdate) {
            currentMenu = <ControlCenter getStyles={this.getStyles} componentName={userComponentName} />
            this.setState({isUpdate: false})
        }
        return (
            <div className={`workspace workspace_${themeContext}`}>
                <div className="workspace__inner">
                    <div className="workspace__content">
                        {currentComponent}
                    </div>
                    {currentMenu}
                </div>
            </div>
        )
    }

    getStyles = (styleType, value) => {
        this.setState({[styleType]: value}, () => {
            console.log(this.state);
        })
    }

    buildClassName = () => {
        let className = '';

        for (let style in this.state) {
            if (style !== 'text' && style !== this.state.currentName) {
                className += `${style}-${this.state[style]} `
            }
        }

        return className;
    }
}

export default Workspace;
