import React, { Component } from 'react';
import '../../assets/css/ControlCenter/Tabs.scss';
import TabBtn from "./TabBtn";

class Tabs extends Component {
    state = {
        currentTabIndex: 0,
    }

    render() {
        const btnNames = ["Style", "Export"];

        const tabButtons = btnNames.map((name, index) => {
            return <TabBtn btnName={name}
                           switchTab={this.switchTab}
                           currentTabIndex={this.currentTabIndex()}
                           key={index}/>
        });

        return(
            <div className="tabs">
                {tabButtons}
            </div>
        )
    }

    switchTab = (tabIndex) => {
        const  {returnTabIndex} = this.props;

        this.setState({
            currentTabIndex: tabIndex,
        })

        returnTabIndex(tabIndex);
    }

    currentTabIndex = () => {
        return this.state.currentTabIndex;
    }
}

export default Tabs;
