import React, { Component } from 'react';
import '../../assets/css/ControlCenter/StyleControl.scss';

class StyleControl extends Component {
    state = {
        value: 'Medium',
    }

    render () {
        return (
            <div className="style-control">
                <div className="style-control__inner">
                    <div className="style-control__wrapper">
                        <form>
                            <label className="style-control__text">Text size</label>
                            <select className="style-control__form"
                                    value={this.state.value}
                                    onChange={this.handleChange}>
                                <option value="Small">Small</option>
                                <option value="Medium">Medium</option>
                                <option value="Large">Large</option>
                            </select>
                        </form>
                    </div>
                </div>
            </div>
        )
    }

    handleChange = (event) => {
        this.setState({value: event.target.value});
    }
}

export default StyleControl;
