import React, {Component} from 'react';
import '../../assets/css/Test2.scss'

class Test2 extends Component {
    render() {
        const params = {background: this.props.background,
                        color: this.props.color,
                        height: 0,
                        width: 0,
                        "border-radius": 0
        }
        const text = this.props.text
        const size = this.props.size;
        if (size === "small") {
            params["height"] = 2 + "vw";
            params["width"] = 7 + "vw";
            params["border"] = 15 + "px"
        } else if (size === "medium") {
            params["height"] = 2 + "vw";
            params["width"] = 7 + "vw";
            params["border-radius"] = 15 + "px"
        } else {
            params["height"] = 2 + "vw";
            params["width"] = 7 + "vw";
            params["border"] = 15 + "px"
        }

        return (
            <button className={"test-btn"} style={params}>{text}</button>
        );
    }
}

export default Test2;
