import '../../assets/css/ControlCenter/Nav.scss';

class NavBtn extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isActive: false,
        }
    }

    render() {
        const {btnName} = this.props;

        return(
            <div className="nav__wrapper">
                <button className="nav__btn"
                        onClick={this.pressBtn}>
                    {btnName}
                </button>
                <div className="nav__underline"/>
            </div>
        )
    }

    pressBtn = () => {
        this.setState({
            isActive: !this.state.isActive,
        })
