import React, { Component } from 'react';
import '../../assets/css/ControlCenter/ControlMenu.scss';
import {text} from "@fortawesome/fontawesome-svg-core";

class StyleMenu extends Component {
    state = {
        text: "Copy",
        isDisabled: false
    }
    render () {
        let currentClass = !this.state.isDisabled ? "control-menu__button" : "control-menu__button control-menu__button_disabled";
        return (
            <div className="control-menu control-menu_row">
                <div>
                    <p className={"control-menu__import"}>To import a component:</p>
                </div>
                <p className={"control-menu__insert"}>Then you can use it:</p>
                <div>
                    <p className={"control-menu__link"} id={"item-to-copy"}>
                        Lorem ipsum dolor sit amet,
                        consectetur adipisicing elit.
                    </p>
                    <button disabled={this.state.isDisabled} onClick={this.copyToClipboard} id={"myInput"} className={currentClass}>
                        {this.state.text}
                    </button>
                </div>
            </div>
        )
    }
     copyToClipboard = () => {
        let str = document.getElementById('item-to-copy').innerText;
        let el = document.createElement('textarea');
        el.value = str;
        document.body.appendChild(el);
        el.select();
        document.execCommand('copy');
        document.body.removeChild(el);
        this.setState({
            text: "Copied",
            isDisabled: true
        })
         setTimeout(this.changeText, 1500)
    }
    changeText = () => {
        this.setState({
            text: "Copy",
            isDisabled: false
        })
    }
}

export default StyleMenu;
