import React, { Component } from 'react';
import '../../assets/css/ControlCenter/ControlMenu.scss';

class StyleMenu extends Component {
    render () {
        return (
            <div className="control-menu control-menu_row">
                <p className={"control-menu__link"} id={"item-to-copy"}>
                    Lorem ipsum dolor sit amet,
                    consectetur adipisicing elit.
                </p>
                <button onClick={this.copyToClipboard} id={"myInput"} className={"control-menu__button"}>Copy</button>
            </div>
        )
    }
     copyToClipboard = () => {
        const str = document.getElementById('item-to-copy').innerText;
        const el = document.createElement('textarea');
        el.value = str;
        el.setAttribute('readonly', '');
        el.style.position = 'absolute';
        el.style.left = '-9999px';
        document.body.appendChild(el);
        el.select();
        document.execCommand('copy');
        document.body.removeChild(el);
    }
}

export default StyleMenu;
